import { GetDataBylist, PostDataByUpdate } from "@/api/marketing";
import { parseTime } from "@/utils";
export default {
  name: "instal",
  data: function data() {
    return {
      listQuery: {
        order_id: null,
        page: 1,
        limit: 10,
        startTime: undefined,
        sstatus: "all"
      },
      total: 0,
      total1: 0,
      total2: 0,
      total3: 0,
      listLoading: true,
      tableData: [],
      dialogVisible: false,
      video: [],
      is_video_status: undefined,
      //视频处理状态
      order_id: undefined
    };
  },
  filters: {
    StrText: function StrText(str) {
      if (str === 1) {
        return "已处理";
      } else {
        return "待处理";
      }
    }
  },
  created: function created() {
    this.fetchList();
  },
  methods: {
    fetchList: function fetchList() {
      var _this = this;

      this.listLoading = true;
      GetDataBylist(this.listQuery).then(function (res) {
        _this.listLoading = false;
        _this.tableData = res.data.data.data;
        _this.total = res.data.data.total;
        _this.total1 = res.data.total1;
        _this.total2 = res.data.total2;
        _this.total3 = res.data.total3;
        console.log(res, "0909");
      });
    },
    handleFilter: function handleFilter() {
      this.listQuery.page = 1;
      this.fetchList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.limit = val;
      this.fetchList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.page = val;
      this.fetchList();
    },
    handleStatusChange: function handleStatusChange(val) {
      this.listQuery.page = 1;
      this.listQuery.sstatus = val;
      this.fetchList();
    },
    headSearch: function headSearch() {
      this.listQuery.page = 1;
      this.fetchList();
    },
    handleClose: function handleClose() {
      this.dialogVisible = false;
    },
    handleEdit: function handleEdit(index, row) {
      this.dialogVisible = true;
      this.video = row.video;
      this.is_video_status = row.is_video_status;
      console.log(row.is_video_status);
      this.order_id = row.id;
    },
    open: function open() {
      this.$message({
        message: "请先选中视频！",
        type: "warning"
      });
    },
    open2: function open2() {
      this.$message({
        message: "恭喜你，已开始下载！",
        type: "success"
      });
    },
    open4: function open4() {
      this.$message.error("下载视频，请重新下载。");
    },
    //下载视频
    headDownload: function headDownload() {
      var _this2 = this;

      var id = this.order_id;
      var idslist = [];
      var chenum = this.video.length;
      var che = 0;

      for (var i = 0; i < this.video.length; i++) {
        if (this.video[i].status === true) {
          che++;
          idslist.push(this.video[i].id);
        } else {
          console.log("还没选择");
        }
      }

      var temp = {
        ids: [],
        id: ""
      };
      temp.ids = idslist.join(",");
      temp.id = this.order_id;

      if (chenum === che) {
        PostDataByUpdate(temp).then(function (res) {
          console.log(res);

          if (res.code === 20000) {
            for (var _i = 0; _i < _this2.video.length; _i++) {
              window.location.href = _this2.video[_i].url;
            }

            _this2.dialogVisible = false;

            _this2.open2();

            _this2.fetchList();
          } else {
            _this2.open4();
          }
        });
      } else {
        this.open();
      }
    }
  }
};
