import { Citylist } from "@/api/city"; // import { regionDataPlus } from 'element-china-area-data'

import { GetSaleByAll } from "@/api/salesman";
import { PostDataByUpdate } from "@/api/shop";
import oneImage from "@/components/Upload/oneImage";
import uploadList from "@/components/Upload/uploadList";
var defaultForm = {};
export default {
  name: "sendOrder",
  data: function data() {
    return {
      env: [],
      cityoptions: [],
      salesman: [],
      op: [{
        label: "普通",
        value: 1
      }, {
        label: "阿帕认证",
        value: 2
      }, {
        label: "阿帕三星",
        value: 3
      }, {
        label: "阿帕四星",
        value: 4
      }, {
        label: "阿帕五星",
        value: 5
      }]
    };
  },
  components: {
    oneImage: oneImage,
    uploadList: uploadList
  },
  computed: {},
  props: {
    postFrom: {
      type: Object,
      default: function _default() {
        return Object.assign({}, defaultForm);
      }
    },
    dialogVisible: {
      type: Boolean,
      default: false
    }
  },
  created: function created() {
    var _this = this;

    var data = this.postFrom.cityCode;
    Citylist().then(function (res) {
      _this.cityoptions = res.data;

      for (var i = 0; i < data.length; i++) {
        data[i] = parseInt(data[i]);
      }

      _this.postFrom.cityCode = data;
    });

    if (!this.postFrom.wechat) {// if (!this.postFrom.wechat.openid) {
      //     this.$message.error("请选绑定openid");
      // }
    }

    GetSaleByAll().then(function (res) {
      _this.salesman = res.data;
      console.log(1, 3, 4);
    });
  },
  methods: {
    handelSave: function handelSave() {
      var _this2 = this;

      this.postFrom.cityCode = this.postFrom.cityCode.join(",");
      var arr = [];

      for (var i = 0; i < this.env.length; i++) {
        arr.push({
          url: this.env[i].url,
          shop_id: this.env[i].shop_id
        });
      }

      var temp = {
        post: this.postFrom,
        env: arr
      };
      PostDataByUpdate(temp).then(function (res) {
        _this2.$emit("handleClose");

        _this2.$message.success("操作成功");
      });
    },
    handleEnv: function handleEnv(e) {
      this.env.push({
        url: e.file.data,
        shop_id: this.postFrom.id
      });
    },
    HandelRemove: function HandelRemove(e) {
      this.env = e;
    },
    handellicense: function handellicense(e) {
      this.postFrom.license = e.file.response.data;
    },
    handleClose: function handleClose() {
      this.$emit("handleClose");
    }
  },
  mounted: function mounted() {
    if (this.dialogVisible) {
      var arr = [];

      for (var i = 0; i < this.postFrom.env.length; i++) {
        arr.push({
          url: this.postFrom.env[i]
        });
      }

      this.env = arr;
    }

    if (this.postFrom.shop_type === "普通") {
      this.postFrom.shop_type = 1;
    }

    this.env = this.postFrom.env;

    if (this.postFrom.shop_type === "阿帕认证") {
      this.postFrom.shop_type = 2;
    }

    if (this.postFrom.shop_type === "阿帕3星") {
      this.postFrom.shop_type = 3;
    }

    if (this.postFrom.shop_type === "阿帕4星") {
      this.postFrom.shop_type = 4;
    }

    if (this.postFrom.shop_type === "阿帕5星") {
      this.postFrom.shop_type = 5;
    }
  }
};
