var self; // import { getList, del, change, delAll, changeAll } from '@/api/admin'

import { change, changeAll, del, delAll, getList } from '@/api/ol';
import waves from '@/directive/waves';
import { getArrByKey, pickerOptions } from '@/utils';
import rolesForm from "./roles/form";
import permissionsForm from "./roles/permissions";
export default {
  name: 'Roles',
  components: {
    rolesForm: rolesForm,
    permissionsForm: permissionsForm
  },
  directives: {
    waves: waves
  },
  filters: {
    statusFilter: function statusFilter(status) {
      var statusMap = {
        0: '禁用',
        1: '正常'
      };
      return statusMap[status];
    }
  },
  data: function data() {
    return {
      tableKey: 0,
      list: null,
      total: null,
      selectedRows: null,
      listLoading: true,
      showSearch: false,
      listQuery: {
        page: 1,
        psize: 10,
        status: '-1',
        title: ''
      },
      buttonDisabled: true,
      deleting: false,
      pickerOptions: pickerOptions,
      currentIndex: -1
    };
  },
  watch: {},
  created: function created() {
    self = this;
    self.fetchList();
  },
  methods: {
    fetchList: function fetchList() {
      self.listLoading = true;
      getList(this.listQuery).then(function (response) {
        self.list = response.data.data;
        self.total = response.data.total;
        self.listLoading = false;
      }); // getListorlse(this.listQuery).then(response => {
      //   self.list = response.data.data
      //   self.total = response.data.total
      //   self.listLoading = false
      // })
    },
    handleFilter: function handleFilter() {
      this.listQuery.page = 1;
      this.fetchList();
    },
    handleFilterClear: function handleFilterClear() {
      this.listQuery = {
        page: 1,
        psize: 10,
        status: '-1',
        title: ''
      };
      this.fetchList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.psize = val;
      this.fetchList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.page = val;
      this.fetchList();
    },
    handleModifyStatus: function handleModifyStatus(index, id, status) {
      this.list[index]['status'] = 1 - status;
      change(id, 'status', 1 - status).then(function (response) {});
    },
    handleSelectionChange: function handleSelectionChange(val) {
      if (val.length > 0) {
        this.buttonDisabled = false;
      } else {
        this.buttonDisabled = true;
      }

      this.selectedRows = val;
    },
    handleCreate: function handleCreate() {
      this.$refs.fromRoles.handleCreate();
    },
    handleUpdate: function handleUpdate(index, id) {
      this.currentIndex = index;
      this.$refs.fromRoles.handleUpdate(id);
    },
    updateRow: function updateRow(temp) {
      if (this.currentIndex >= 0 && temp.id > 0) {
        this.list.splice(this.currentIndex, 1, temp);
      } else {
        if (this.list.length >= 10) {
          this.list.pop();
        }

        this.list.push(temp);
        this.total = this.total + 1;
      }

      this.currentIndex = -1;
    },
    handleDelete: function handleDelete(index, id) {
      var _this = this;

      this.$confirm('此操作将永久删除该记录, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this.$set(_this.list[index], 'delete', true);

        del(id).then(function (response) {
          if (response.status === 1) {
            _this.list.splice(index, 1);

            _this.total = _this.total - 1;

            _this.$notify.success(response.msg);
          } else {
            _this.$notify.error(response.msg);
          }

          _this.$set(_this.list[index], 'delete', false);
        }).catch(function (error) {
          _this.$set(_this.list[index], 'delete', false);
        });
      }).catch(function () {
        _this.$message({
          type: 'info',
          message: '已取消删除'
        });
      });
    },
    handleDeleteAll: function handleDeleteAll() {
      var _this = this;

      if (this.selectedRows.length > 0) {
        this.$confirm('此操作将永久删除该记录, 是否继续?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          _this.deleting = true;
          var ids = getArrByKey(_this.selectedRows, 'id');
          var idstr = ids.join(',');
          delAll({
            ids: idstr
          }).then(function (response) {
            if (response.status === 1) {
              var delindex = [];

              _this.list.forEach(function (item, index, input) {
                if (ids.indexOf(item.id) > -1) {
                  delindex.push(index);
                }
              });

              for (var i = delindex.length - 1; i >= 0; i--) {
                _this.list.splice(delindex[i], 1);
              }

              _this.total = _this.total - delindex.length;

              _this.$message.success(response.msg);
            } else {
              _this.$message.error(response.msg);
            }

            _this.deleting = false;
          }).catch(function (error) {
            _this.deleting = false;
          });
        }).catch(function () {
          _this.$message({
            type: 'info',
            message: '已取消删除'
          });
        });
      } else {
        _this.$message.error('请选择要删除的数据');
      }
    },
    handleCommand: function handleCommand(command) {
      var _this = this;

      if (this.selectedRows.length > 0) {
        var ids = getArrByKey(this.selectedRows, 'id');
        var idstr = ids.join(',');
        changeAll({
          val: idstr,
          field: 'status',
          value: command
        }).then(function (response) {
          if (response.status === 1) {
            _this.list.forEach(function (item, index, input) {
              if (ids.indexOf(item.id) > -1) {
                _this.list[index]['status'] = command;
              }
            });

            _this.$message.success(response.msg);
          } else {
            _this.$message.error(response.msg);
          }
        }).catch(function (error) {});
      } else {
        _this.$message.error('请选择要操作的数据');
      }
    }
  }
};
