result:
.w-dia {
  .el-dialog__body {
    padding: 1px 20px;
    padding-left: 35px;
  }
  .el-dialog__title {
    font-size: 14px;
  }
}

.dia-content {
  display: flex;
  font-family: "Helvetica Neue", Helvetica, "PingFang SC", "Hiragino Sans GB",
    "Microsoft YaHei", "微软雅黑", Arial, sans-serif;
  font-size: 12px;
  flex-direction: row;
  flex-wrap: wrap;
  .c-data {
    width: 50%;
    margin-bottom: 10px;
    .c-title {
      font-weight: 800;
      margin-bottom: 10px;
    }
    .el-select--mini {
      width: 100%;
    }
    .el-cascader--mini {
      width: 100%;
      .el-input__suffix {
        right: -6px;
      }
    }
    .ct-item {
      display: flex;
      flex-direction: row;

      align-items: center;
      margin-bottom: 10px;
      .d_code {
        display: flex;
        flex-direction: row;
        width: 60%;
        .el-select {
          width: 50%;
        }
      }
      .label {
        width: 23%;
        margin-right: 10px;
        /*text-align-last: justify;*/
        width: 80px;
      }
    }
  }
  .conent {
    width: 100% !important;
    .ct-item {
      .label {
        width: 70px !important;
      }
      .textarea {
        width: 80%;
      }
    }
  }
}

.n-post {
  .el-dialog__body {
    padding: 10px 10px;
    .el-form-item__label {
      font-weight: 400;
      font-size: 12px;
    }
  }
}

.credentials-img {
  width: 150px;
  height: 100px;
  border-radius: 30px;
}

.dialog-footer {
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  padding-bottom: 20px;
}

.el-upload--picture-card i {
  margin-left: 19px;
}

textarea {
  text-align-last: auto;
}

.c-input {
  width: 70%;
}
