.bartar {
  overflow-y: auto;
  display: flex;
  height: 100%;
  background: #304156;
  padding-bottom: 100px;
}

.broadside {
  width: 100px;
  overflow-y: auto;

  border-right: 1px solid #999999;
}

.broadside::-webkit-scrollbar {
  display: none;
}

.broadside .br—item {
  height: 85px;
  overflow: hidden;
  color: #999999;
}

.broadside .br-active {
  color: #ffffff;
  position: relative;
}

.broadside .br-active::before {
  content: '';
  width: 2px;
  height: 12px;
  position: absolute;
  top: 36px;
  right: 5px;
  background: #009CFF;
  cursor: pointer;
}

.br—item .item-img {
  text-align: center;

  font-size: 15px;
  margin-top: 20px;
  margin-bottom: 10px;

}

.br—item .item-name {
  font-size: 7px;
  font-weight: bold;
  text-align: center;
}

.brlist .brlist-name {
  width: 110px;
  height: 50px;
  line-height: 50px;
  color: #999999;
  font-size: 7px;
  font-weight: bold;
  margin-left: 30px;
  cursor: pointer;
}

.brlist .brlist-active {
  color: #ffffff;
  position: relative;
}

.brlist .brlist-active::before {
  content: '';
  width: 2px;
  height: 12px;
  position: absolute;
  top: 19px;
  left: -6px;
  background: #009CFF;
}

.brlist {

  .child {
    height: 30px !important;
    line-height: 30px !important;
    padding-left: 10px;

  }

  .child::before {
    content: '';
    width: 2px;
    height: 12px;
    position: absolute;
    top: 9px !important;
    left: 3px;
    background: #009CFF;
  }

}
