.el-select-dropdown__item {
  color: #000;
  font-size: 12px;
}

.income-search {
  display: flex;
  box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
  padding: 8px 20px;
  margin: 10px 5px;
  border-radius: 4px;
  .search-item >>> .el-input__inner {
    height 26px;
    font-size: 12px;
    padding: 0 10px;
  }
  .search-item {
    display: flex;
    align-items center;
    margin-right: 26px;
    width: 20%;
    .label {
      font-size: 12px;
      padding-right: 4px;
      color: #333;
      font-weight: 400;
      width 40%;
    }
    .search-btn {
      width: 30%;
      background: #009CFF;
      border: 1px solid #f5f5f5;
      text-align: center;
      border-radius: 4px;
      font-size: 12px;
      padding: 5px 1px;
      color: #fff;
      &:hover {
        cursor: pointer;
      }
    }
  }

  .search-item >>> .el-input__icon {
    line-height 26px;
  }
  .search-item >>> .el-input__prefix {
    display none;
  }

}

.rate-content {
  margin: 20px 5px;
  border: 1px solid #f5f5f5;
  box-shadow: 0 0 5px 0 rgba(53, 53, 53, .28);
  .select-bar {
    display: flex;
    padding: 0 20px;

    border-bottom: 1px solid #f5f5f5;
    .select-item {
      margin-right: 20px;
      font-size: 12px;
      color: #333;
      font-weight: 500;
      padding: 8px 0;
      border-bottom: 2px solid transparent;
      transition all .6s;
      &:hover {
        cursor: pointer;
      }
      span {
        &:nth-child(2) {
          color: #CC0000;
          margin-left: 4px;
        }
      }

    }
    .active-item {
      border-bottom: 2px solid #009CFF;
      color: #009CFF;
      span {
        color: #009CFF !important;
      }
    }
  }
  .rate-item {
    display: flex;
    align-items center;
    border-bottom: 1px solid #f5f5f5;
    .rate-info {
      width: 50%;
      padding: 20px 28px;
      .rate-text {
        font-size: 12px;
        color: #000;
        margin-bottom: 16px;
        line-height: 18px;
        font-weight: 500;
      }
      .rate-img {
        display: flex;
        align-items: center;
        .img-item {
          width: 16%;
          img {
            width: 60px;
            height: 60px;
            border-radius: 4px;
          }
        }
      }
      .rate-time {
        color: #666666;
        font-size: 12px;
        margin: 10px 0;
        font-weight: 400;
        span {
          margin-right: 20px;
          &:first-child {
            margin-right: 40px;
          }
        }
      }
    }
    .rate-score {
      width: 30%;
      .product {
        display: flex;
        align-items center;
        margin: 20px 0;
        font-size: 12px;
        padding-left: 30px;
        .item-label {
          margin-right: 10px;
          font-weight: 500;
          color: #000
        }
        .item-name {
          margin-left: 20px;
          font-weight: 500;
          color: #000
        }
      }

    }
    .rate-user {
      width: 20%;
      display: flex;
      justify-content: center;
      .user-avatar {
        width: 20%;
        margin-right 10px;
        img {
          width: 40px;
          height: 40px;
          border-radius: 50%;
        }
      }
      .nick-name {
        font-size: 14px;
        color: #000;
        font-weight: 500;
      }
      .real-name {
        color: #666666;
        font-size: 12px;
        font-weight: 500;
        margin-top: 8px;
      }
    }
  }
}

.income-pagination {
  text-align center;
  margin: 20px 0;
}
