import { GetInstallByList } from '@/api/shop'
import OrderInfo from './order/OrderInfo'

export default {
  name: 'Anzhuang',
  props: {
    id: {
      type: String,
      default: 0
    }
  },
  components:{
    OrderInfo
  },
  data() {
    return {
      postFrom:{},
      infodialogVisible:false,
      order_num: '',
      status: '',
      date: '',
      rate: '',
      rowStyle: {
        fontSize: '12px',
        fontWeight: '400',
        color: '#333'
      },
      total: 0,
      sumcost: 0,
      listQuery: {
        page: 1,
        limit: 10,
        status: undefined,
        title: ''
      },
      headStyle: {
        fontSize: '12px',
        fontWeight: '500',
        color: '#000'
      },
      options: [
        {
          value: '2',
          label: '待确认（门店）'
        }, {
          value: '3',
          label: '待安装'
        }, {
          value: '4',
          label: '已安装'
        }, {
          value: '5',
          label: '财审1'
        }, {
          value: '6',
          label: '财审2'
        },
        {
          value: '7',
          label: '待结算'
        },
        {
          value: '8',
          label: '已结算'
        }
      ],
      tableData: [],
      currentPage: 1
    }
  },
  created() {
    this.fetchList()
  },
  methods: {

    handelInfoDialog() {
      this.infodialogVisible = !this.infodialogVisible
    },
    fetchList() {
      this.listQuery.id = this.id

      GetInstallByList(this.listQuery).then(res => {
        this.tableData = res.data.data
        this.total = res.data.total
        this.sumcost = res.data.sumcost
      })
    },
    handleFilterClear() {
      this.listQuery.page=1
      this.fetchList()
    },

    //打开详情
    handelInfo(row) {

      console.log(row)
      this.postFrom = row
      if (row.city_code.constructor === String) {
        this.postFrom.city_code = this.postFrom.city_code.split(',')

      }

      if (row.car_model.constructor === String) {
        this.postFrom.car_model = this.postFrom.car_model.split(',')
      }

      if (row.lazada_list.constructor === String) {
        this.postFrom.lazada_list = this.postFrom.lazada_list.split(',')
      }
      let lazada = this.postFrom.lazada_list
      this.postFrom.lazada_list = []

      for (let i = 0; i < lazada.length; i++) {
        this.postFrom.lazada_list.push(parseInt(lazada[i]))
      }
      this.infodialogVisible = !this.infodialogVisible

    },

    handleSizeChange(val) {
      this.listQuery.limit = val
      this.fetchList()
    },
    handleCurrentChange(val) {
      this.listQuery.page = val
      this.fetchList()
    },
    handleSizeChange(val) {
      console.log(`每页 ${val} 条`)
    },
    handleCurrentChange(val) {
      console.log(`当前页: ${val}`)
    },

    clickToSearch() {
      let temp = {
        order_num: this.order_num,
        status: this.status,
        date: this.date,
        rate: this.rate
      }
      console.log(temp)
    },
    deleteRow(index, rows) {
      console.log(index, rows)
    }
  }
}
