.pageclass {
  .pagination-container {
    position: fixed;
    bottom: 0;
    padding-top: 4px;
    padding-bottom: 9px;
    background: #ffffff;
    width: 100%;
    padding-left: 20px;
    /*text-align: c;*/
  }
}

.show-discount {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, 0.4);
  z-index: 9;
  .discount-warp {
    width: 400px;

    background: #ffffff;
    margin: 100px auto 0;
    padding: 20px;
    border-radius: 5px;
    position: relative;
    .close-icon {
      position: absolute;
      right: 20px;
      top: 10px;
      cursor: pointer;
      i {
        font-size: 13px;
      }
    }
    .title {
      font-size: 12px;
    }
    .money {
      display: flex;
      font-size: 12px;
      align-items: center;
      margin-top: 15px;
      .money-name {
        display: flex;
        span {
          display: block;
        }
        .name-span {
          width: 50px;
          display: block;
          text-align-last: justify;
        }
      }

      .input-money {
        width: 70%;
        margin-left: 10px;
      }
      /deep/ .el-input--medium .el-input__inner {
        height: 30px !important;
        line-height: 30px;
        font-size: 12px;
      }
      .radio {
        margin-left: 10px;
        width: 70%;
        /deep/ .el-radio {
          margin-right: 10px;
        }
        /deep/ .el-radio__label {
          font-size: 12px;
        }
      }
      .condition {
        display: flex;
        height: 30px;
        line-height: 30px;
        margin-left: 10px;
        cursor: pointer;
        .condition-navbar {
          padding: 0 12px;
          border: 1px solid #dcdcdc;
        }
        .condition-navbar-active {
          padding: 0 12px;
          border: 1px solid #2489fe;
          color: #2489fe;
        }
      }

      .impose {
        display: flex;
        height: 30px;
        line-height: 30px;
        margin-left: 10px;
        .input-money {
          width: 25%;
          margin-left: 10px;
          margin-right: 10px;
        }
        /deep/ .el-input--medium .el-input__inner {
          height: 30px !important;
          line-height: 30px;
        }
      }
      .yx {
        margin-left: 25px;
      }

      .cuo {
        margin-left: 22px;
        .data-picker {
          width: 90%;
          height: 30px;
          line-height: 30px;
        }
        /deep/ .el-range-editor--medium .el-range__icon {
          line-height: 22px;
        }
        /deep/ .el-range-editor--medium .el-range-separator {
          line-height: 22px;
        }

        /deep/ .el-range-editor--medium .el-range-input {
          font-size: 12px;
        }
      }
      .textarea {
        width: 70%;
        margin-left: 10px;
        /deep/ .el-input--medium {
          font-size: 12px;
        }
      }
    }
    .new_botton {
      margin-top: 30px;
      display: flex;
      flex-direction: row;
      justify-content: flex-end;
    }
  }
}

.el-date-editor {
  width: 88%;
  font-size: 12px;
  color: #666;
  font-weight: 500;
}

.income-wrap > > > .el-date-editor input.el-input__inner {
  padding: 0;
  color: #666;
  font-size: 12px;
  height: 20px;
  text-align: center;
  border: none;
}

.income-wrap > > > .el-date-editor .el-input__prefix {
  display: none;
}

.income-wrap > > > .el-date-editor .el-input__suffix {
  display: none;
}

.mask {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, 0.4);
  z-index: 9;

  .mask-box {
    background: #fff;
    margin: 10% auto;
    width: 24%;
    border-radius: 8px;

    .mask-title {
      display: flex;
      padding: 14px 10px;
      font-size: 14px;
      color: #333;

      span {
        width: 50%;

        &:nth-child(2) {
          text-align: right;
          padding-right: 10px;

          &:hover {
            cursor: pointer;
          }
        }
      }
    }

    .mask-content {
      padding: 17px;
      color: #333;
      font-size: 12px;

      .mask-detail {
        display: flex;
        margin: 10px 0;
        align-items: center;

        .detail-text {
          width: 24%;
        }

        .detail-value {
          width: 76%;
          background: #eeeeee;
          padding: 5px 0 5px 10px;
        }
      }

      .special {
        .detail-value {
          height: 50px;
        }
      }
    }

    .mask-btns {
      display: flex;
      margin-left: 70%;
      width: 26%;
      padding: 14px 0;
      font-size: 12px;

      .confirm {
        border: 1px solid #2589ff;
        background: #2589ff;
        color: #fff;
        padding: 4px 12px;
        border-radius: 4px;

        &:hover {
          cursor: pointer;
        }
      }

      .cancel {
        border: 1px solid #999999;
        background: #fff;
        color: #999999;
        padding: 4px 12px;
        border-radius: 4px;
        margin-left: 20px;

        &:hover {
          cursor: pointer;
        }
      }
    }
  }
}

.el-select-dropdown__item {
  color: #000;
  font-size: 12px;
}

.zichan-container {
  padding: 30px 0;

  .income-container {
    display: flex;
    justify-content: space-between;
    .income-wrap {
      background: #fff;
      box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.1);
      border-radius: 4px;
      margin: 5px;
      width: 24%;

      .income-item {
        overflow: hidden;
        padding-bottom: 10px;

        .item-top {
          overflow: hidden;
          text-align: center;
          color: #666;
          font-size: 12px;
          margin-bottom: 5px;
          min-height: 57px;
          font-weight: 500;

          span {
            font-size: 12px;
            margin-left: 5px;
          }

          .top-title {
            color: #333;
            font-size: 14px;
            margin: 14px 0 4px;
            font-size: 12px;
          }
        }

        .new-ticket {
          background: #009bff;
          width: 30%;
          margin: 30px auto 0 !important;
          padding: 5px 0;
          border-radius: 4px;
          font-size: 12px;
          color: #fff !important;

          &:hover {
            cursor: pointer;
          }
        }

        .item-num {
          margin: 5px 0;
          display: flex;
          padding: 0 4px 0 20px;
          font-size: 12px;
          align-items: center;

          span {
            display: inline-block;
            width: 50%;

            span {
              margin-left: 5px;
            }

            &:nth-child(2) {
              text-align: right;
              padding-right: 10px;
              color: #cc0000;
              font-size: 14px;
              /*font-family: fantasy;*/
            }
          }
        }

        .records {
          text-align: center;
          justify-content: center;
          padding: 0;
          color: #009bff;
          margin-top: 20px;

          &:hover {
            cursor: pointer;
          }
        }

        .total {
          span {
            &:nth-child(2) {
              width: 60%;
              font-size: 18px;
              font-weight: 500;
            }

            &:nth-child(3) {
              width: 25%;
              text-align: center;
              color: #009cff;

              &:hover {
                cursor: pointer;
              }
            }
          }
        }
      }
    }
  }

  .income-form {
    margin: 20px 5px;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.1);
    padding: 14px 20px;
    border-radius: 4px;

    .income-search {
      display: flex;

      .search-item > > > .el-input__inner {
        height: 26px;
        font-size: 12px;
        padding: 0 10px;
      }

      .search-item {
        display: flex;
        align-items: center;
        // margin-right: 15px;
        width: 23%;
        .label {
          font-size: 12px;
          padding-right: 4px;
          color: #333;
          font-weight: 400;
        }
        .search-btn {
          width: 30%;
          background: #009cff;
          border: 1px solid #f5f5f5;
          text-align: center;
          border-radius: 4px;
          font-size: 12px;
          padding: 5px 1px;
          color: #fff;

          &:hover {
            cursor: pointer;
          }
        }
      }
      .search-item:nth-child(1) {
        margin-left: 3%;
      }
      .search-item:nth-child(4) {
        width: 20%;
      }

      .search-item > > > .el-input__icon {
        line-height: 26px;
      }

      .search-item > > > .el-input__prefix {
        display: none;
      }
    }

    .income-table {
      margin: 20px 0;
    }
  }

  .ticket-wrap {
    padding: 20px 28px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;

    .ticket-item {
      width: 47%;
      border: 1px solid #ffebe2;
      margin: 15px 4px 0;
      background: #ffebe2;
      border-radius: 15px;
      position: relative;

      &:hover {
        cursor: pointer;
      }

      .ticket-owner {
        position: absolute;
        top: 15px;
        right: 30px;
        font-size: 11px;
        color: #ea3756;
      }

      .ticket-icon {
        position: absolute;
        right: 40%;
        bottom: 5px;
        i {
          font-size: 12px;
          margin-right: 10px;
        }
      }

      .ticket-img {
        position: absolute;
        bottom: 8px;
        right: 25px;
        font-size: 12px;
        color: #ea3756;
        width: 55px;

        img {
          width: 100%;
        }
      }

      .ticket-top {
        display: flex;
        padding: 8px 20px 5px;
        font-size: 11px;
        color: #ea3756;
        align-items: baseline;

        .ticket-num {
          margin-right: 20px;

          span {
            font-size: 25px;
            font-weight: 500;
            margin-left: 4px;
            color: #ea3756;
            /*font-family: fantasy;*/
          }
        }

        .ticket-addition {
          font-weight: 500;
          color: #ea3756;
        }
      }

      .ticket-center {
        font-size: 10px;
        padding: 0 20px 20px;
        color: #333;
        font-weight: 400;
        overflow: hidden;
        width: 80%;

        .ticket-comment {
          margin: 4px 0;
          line-height: 15px;
        }
      }

      .ticket-bottom {
        position: absolute;
        bottom: 0px;
        .ticket-expir {
          font-size: 10px;
          padding: 0 20px 6px;
          margin: 5px 0;
          color: #666666;
        }
      }
    }
  }

  .income-pagination {
    text-align: center;
    margin: 20px 0;
  }
}
