result:
.el-cascader {
  width 100%
}

.dialog-footer {
  justify-content: center;
}

.admin-introduce {
  display: flex;
  margin: 25px 0 20px;
  padding: 8px 24px;
  font-size: 12px;
  align-items: center;
  background #fff;
  box-shadow: 0 0 4px 2px rgba(0, 0, 0, 0.1);
  border-radius: 4px;
  .ad-id {
    width: 12%;
    span {
      padding-right: 10px;
      color: #666666;
    }
  }
  .ad-type {
    width: 18%;
    span {
      padding-right: 10px;
      color: #666;
    }
    .special {
      color: #0000ff;
      font-weight: 500;
    }
  }
  .ad-update {
    display: flex;
    align-items center;
    width: 25%;
    span {
      padding-right: 10px;
    }
  }
  .ad-score {
    width: 13%;
    span {
      padding-right: 10px;
    }
  }
  .ad-recom >>> .el-checkbox__label {
    font-size: 12px;
  }
}

.shop-info {
  padding: 20px 38px;
  box-shadow: 0 0 10px 0 rgba(153, 153, 153, 0.1);
  border-radius: 6px;
  .info-wrap {
    display: flex;
    flex-wrap: wrap;
    .info-img {
      width: 6%;
      img {
        width: 100%;
        border-radius: 4px;
      }
    }
    .info-item-wrap {
      width: 88%;
      display: flex;
      flex-wrap: wrap;
      align-items: center;
      padding: 0 30px;
      font-size: 12px;
      .info-item {
        margin-bottom: 14px;
        .item-left {
          color: #666;
          font-weight: 400;
        }
        .item-right {
          color: #333;
          font-weight: 400;
        }
      }
      .nick-name, .owner {
        width: 20%;
      }
      .contact, .address {
        width: 30%;
      }
      .waiter, .server {
        width: 20%;
      }
      .create-time, .open-id {
        width: 30%;
      }
      .notes {
        width: 100%
        display: flex;
        span.item-right {
          width: 40%;
          line-height: 16px;
        }
      }
    }
  }
  .btn-wrap {
    display: flex;
    width: 55%;
    margin-left: 40%;
    justify-content: flex-end;
    .btns {
      background: #009cff;
      color: #fff;
      margin-right: 20px;
      width: 16.6%;
      font-size: 12px;
      text-align: center;
      padding: 5px 0;
      border-radius: 4px;
      &:hover {
        cursor: pointer;
      }

    }
  }
}

.shop-detail {
  display: flex;
  margin: 20px 0;
  padding: 20px 38px;
  box-shadow: 0 0 10px 0 rgba(153, 153, 153, .1);
  .detail-text-wrap {
    width: 50%;
    padding: 0 35px 0 0;
    .lt-detail {
      font-size: 12px;
      color: #333;
      .lt-item {
        margin: 16px 0;
        display: flex;
        .left-lt {
          min-width: 16%;
        }
      }
    }
    .bg-detail {
      .bg-detail-title {
        font-size: 14px;
        color: #333;
        margin: 10px 0;
      }
      .bg-detail-content {
        font-size: 12px;
        color: #333;
        line-height: 18px;
      }
    }

  }
  .detail-img-wrap {
    width: 50%;
    .img-wrap {
      display: flex;
      flex-wrap: wrap;
      margin: 14px 0;
      .img-item {
        width: 15%;
        margin-right: 30px;
        img {
          width: 83px;
          height: 83px;
          border-radius: 6px;
        }
      }
    }
    .img-wrap-title {
      font-size: 14px;
      color: #333;
    }

  }
}

.info >>> .el-dialog__body {
  padding-top: 5px;
}

.c-input {
  width 70%;
}

.ad-recom >>> .el-checkbox {
  margin-right: 15px !important;
}
