import {GetHomeByData, GetOrderByservice, GetOrderByShop, GetOrderByServer} from '@/api/home'
import TableView from './table/index'

export default {
    name: 'Index',
    data() {
        return {

            ordersum: 0,
            timesum: 0,
            tabactiveName: 'shop',
            activeName: 'shop',
            datalist: [],
            total: 0,
            listQuery: {
                limit: 20,
                page: 1
            },
            statistical: {
                order: 0,
                Demand: 0
            },
            order: [],
        }
    },
    created() {
        this.getdata()
    },
    components: {
        TableView
    },
    methods: {
        handleFilter() {
            let that = this;
            if (this.activeName === 'shop') {
                that.order = []
                GetOrderByShop(this.listQuery).then(res => {
                    that.order = res.data.data
                    that.ordersum = res.data.star
                    that.timesum = res.data.starstar
                })
            }
            if (this.activeName === 'product') {
                that.order = []
                GetOrderByservice(this.listQuery).then(res => {
                    that.order = res.data
                })
            }
            if (this.activeName === 'order') {
                that.order = []
                GetOrderByServer(this.listQuery).then(res => {
                    that.order = res.data
                })
            }
        },
        handleSizeChange(val) {
            this.listQuery.limit = val
            this.handelShop()
        },
        handleCurrentChange(val) {
            this.listQuery.page = val
            this.handelShop()
        },

        handleView(row, star) {
            this.$refs.tableview.handleView(row, this.tabactiveName, star);
        },
        handleTab(e) {
            let that = this;
            this.activeName = e.name
            that.order = []
            that.total = 0;
            if (e.name === 'shop') {

                GetOrderByShop(this.listQuery).then(res => {
                    that.order = res.data.data.data
                    that.total = res.data.data.total
                    that.ordersum = res.data.star
                    that.timesum = res.data.starstar
                })
            }
            if (e.name === 'product') {
                that.order = []
                GetOrderByservice(this.listQuery).then(res => {
                    that.order = res.data.data;
                    that.total = res.data.total
                })
            }
            if (e.name === 'order') {
                that.order = []
                GetOrderByServer(this.listQuery).then(res => {
                    that.order = res.data.data
                    that.total = res.data.total
                })
            }
        },
        handleClick(e) {
            this.getdata();
        },
        getdata() {
            let that = this;
            GetOrderByShop(this.listQuery).then(res => {
                console.log(res)
                that.order = res.data.data.data
                that.total = res.data.data.total
                that.ordersum = res.data.star
                that.timesum = res.data.starstar
            })

        }
    }
}
