.upload-container > > > .el-upload--picture-card {
  width: 70px;
  height: 70px;
  line-height: 90px;
}

.upload-container > > > .el-upload-list--picture-card .el-upload-list__item {
  width: 70px;
  height: 70px;
}

.upload-container {
  /*width: 70px;*/
  height: 70px;
}

.image-uploader {
  .el-upload {
    border: 1px dashed #d9d9d9;
    border-radius: 6px;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    width: 70px;
    height: 70px;
    .el-upload-dragger {
      height: 100%;
    }
    &:hover {
      border-color: #409EFF;
    }
  }
  .el-upload-list__item {
    width: 160px;
    height: 160px;
  }
  .image {
    width: 178px;
    height: 178px;
    display: block;
  }
  .image-uploader-icon {
    font-size: 28px;
    color: #8c939d;
    width: 70px;
    height: 70px;
    line-height: 70px;
    text-align: center;
  }

}
