.mask {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, .84);
  z-index: 9;
  .mask-box {
    background: #fff;
    margin: 10% auto;
    width: 24%;
    border-radius: 8px;
    .mask-title {
      display: flex;
      padding: 14px 10px;
      font-size: 14px;
      color: #333;
      span {
        width: 50%;
        &:nth-child(2) {
          text-align: right;
          padding-right: 10px;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
    .mask-content {
      padding: 17px;
      color: #333;
      font-size: 12px;
      .mask-detail {
        display: flex;
        margin: 10px 0;
        align-items center;
        .detail-text {
          width: 24%;
        }
        .detail-value {
          width: 76%;
          background: #EEEEEE;
          padding: 5px 0 5px 10px;
        }
      }
      .special {
        .detail-value {

          height: 50px;
        }
      }
    }
    .mask-btns {
      display: flex;
      margin-left: 70%;
      width: 26%;
      padding: 14px 0;
      font-size: 12px;
      .confirm {
        border: 1px solid #2589FF;
        background #2589FF;
        color: #fff;
        padding: 4px 12px;
        border-radius: 4px;
        &:hover {
          cursor: pointer;
        }
      }
      .cancel {
        border: 1px solid #999999;
        background #fff;
        color: #999999;
        padding: 4px 12px;
        border-radius: 4px;
        margin-left: 20px;
        &:hover {
          cursor: pointer;
        }
      }
    }
  }

}

.rate-content {
  margin: 20px 5px;
  border: 1px solid #f5f5f5;
  box-shadow: 0 0 5px 0 rgba(53, 53, 53, .28);
  .select-bar {
    display: flex;
    padding: 0 20px;

    border-bottom: 1px solid #f5f5f5;
    .select-item {
      margin-right: 20px;
      font-size: 12px;
      color: #333;
      font-weight: 500;
      padding: 8px 0;
      border-bottom: 2px solid transparent;
      transition all .6s;
      &:hover {
        cursor: pointer;
      }
      span {
        &:nth-child(2) {
          color: #CC0000;
          margin-left: 4px;
        }
      }

    }
    .active-item {
      border-bottom: 2px solid #009CFF;
      color: #009CFF;
      span {
        color: #009CFF !important;
      }
    }
  }
  .ticket-wrap {
    padding: 20px 28px;
    display: flex;
    flex-wrap: wrap;
    justify-content space-between;
    .ticket-item {
      width: 47%;
      border: 1px solid #FFEBE2;
      margin: 20px 4px;
      background: #FFEBE2;
      border-radius: 20px;
      position: relative;
      &:hover {
        cursor: pointer;
      }
      .ticket-owner {
        position: absolute;
        top: 20px;
        right: 30px;
        font-size: 12px;
        color: #EA3756;
      }
      .ticket-img {
        position: absolute;
        bottom: 8px;
        right: 25px;
        font-size: 12px;
        color: #EA3756;
        width: 60px;
        img {
          width: 100%;
        }
      }
      .ticket-top {
        display: flex;
        padding: 20px 20px 10px;
        font-size: 12px;
        color: #EA3756;
        align-items: baseline;

        .ticket-num {
          margin-right: 20px;
          span {
            font-size: 25px;
            font-weight: 500;
            margin-left: 4px;
            color: #EA3756;
          }
        }
        .ticket-addition {
          font-weight: 500;
          color: #EA3756;
        }
      }
      .ticket-center {
        font-size: 12px;
        padding: 0 20px;
        color: #333;
        font-weight: 400;
        overflow: hidden;
        .ticket-comment {
          margin: 4px 0;
        }
      }
      .ticket-bottom {
        .ticket-expir {
          font-size: 12px;
          padding: 0 20px 8px;
          margin: 10px 0;
          color: #666666;
        }

      }
    }
  }

}

.income-pagination {
  text-align center;
  margin: 20px 0;
}
