.zichan-container {
  padding: 30px 0;
  .income-container {
    display: flex;
    .income-wrap {
      background: #fff;
      box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
      border-radius: 4px;
      margin: 5px;
      width: 25%;
      .income-item {
        overflow: hidden;
        padding-bottom: 10px;
        .item-top {
          overflow: hidden;
          text-align center;
          color: #CC0000;
          font-size: 18px;
          margin-bottom: 20px;
          min-height: 57px;
          font-weight: 500;
          span {
            font-size: 12px;
          }
          .top-title {
            color: #333;
            font-size: 14px;
            margin: 14px 0 10px;
            font-size: 12px;
          }
        }
        .item-num {
          margin: 8px 0;
          display: flex;
          padding: 0 4px 0 20px;
          font-size: 12px;
          align-items center;
          span {
            display: inline-block;
            width: 50%;
            &:nth-child(2) {
              text-align: right;
              padding-right: 10px;
              color: #CC0000;
              font-size: 14px;
            }
          }
        }
        .total {
          span {
            &:nth-child(2) {
              width: 60%;
            }
            &:nth-child(3) {
              width: 25%;
              text-align center;
              color: #009CFF;
              &:hover {
                cursor: pointer;
              }

            }
          }
        }
      }
    }
  }
  .income-form {
    margin: 20px 5px;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
    padding: 14px 20px;
    border-radius: 4px;
    .income-search {
      display: flex;
      .search-item >>> .el-input__inner {
        height 26px;
        font-size: 12px;
        padding: 0 10px;
      }
      .search-item {
        display: flex;
        align-items center;
        margin-right: 26px;
        /*width: 18%;*/
        .label {
          font-size: 12px;
          padding-right: 4px;
          color: #333;
          font-weight: 400;
          width 30%;
        }
        .value {
          width 80%;
        }
        .search-btn {
          width: 30%;
          background: #009CFF;
          border: 1px solid #f5f5f5;
          text-align: center;
          border-radius: 4px;
          font-size: 12px;
          padding: 5px 1px;
          color: #fff;
          &:hover {
            cursor: pointer;
          }
        }
      }

    }
    .income-table {
      margin: 20px 0;
    }
  }
  .income-pagination {
    text-align center;
    margin: 20px 0;
  }
  .el-date-editor {
    width 180px !important;
    .el-input__inner {
      padding: 0 30px !important;
    }
  }
}
