import { GetSenlogByList } from '@/api/platformcoupon'
import { Citylist } from '@/api/city'
import waves from '@/directive/waves'
import { GetDataByShopAll } from "@/api/shop";

export default {
  directives: {
    waves
  },
  data() {
    return {
      shop:[],
      listQuery: {
        page: 1,
        limit: 10,
        status: 'all',
        title: '',
        sstatus: 'all',
        nickname: '',
        contact: '',
        cityCode: ''
      },
      sumprice:0,
      list: null,
      total: 1,
      checked: true,
      listLoading: true,
      cityoptions: [] // 地址
    }
  },

  created() {
    GetDataByShopAll().then(res => {
      this.shop = res.data;
    })
    this.fetchList()
  },
  methods: {
    fetchList() {
      this.listLoading = true
      GetSenlogByList(this.listQuery).then(response => {
        this.list = response.data.data
        this.total = response.data.total
        this.sumprice=response.data.sumprice
        this.listLoading = false

      })
    },
    handleFilter() {
      this.listQuery.page = 1
      this.fetchList()
    },
    handleSizeChange(val) {
      this.listQuery.psize = val
      this.fetchList()
    },


    handleCurrentChange(val) {
      this.listQuery.page = 1
      this.listQuery.sstatus = val
      this.fetchList()
    }
  }
}
