import { GetProjectBy, PostBussByUpdate } from "@/api/shop";

const defaultForm = {};
export default {
  name: "index",
  props: {
    dialogVisible: {
      type: Boolean,
      default: true
    },
    id: {
      type: String,
      default: 0
    },
    postFrom: {
      type: Object,
      default: function() {
        return Object.assign({}, defaultForm);
      }
    }
  },
  data() {
    return {
      main: [],
      serve: [],
      options: [
        {
          label: "9:00-22:00",
          value: "9:00-22:00"
        }
      ]
    };
  },
  created() {
    GetProjectBy(this.postFrom.id).then(res => {
      console.log(res);
      this.main = res.data.main;
      this.serve = res.data.serve;
    });
  },
  methods: {
    handleSave() {
      var data = {
        business_time: this.postFrom.business_time,
        slogan: this.postFrom.slogan,
        id: this.postFrom.id
      };
      var temp = {
        data,
        main: this.main,
        serve: this.serve
      };
      PostBussByUpdate(temp).then(res => {
        this.$emit("handleClose");
        this.$message.success("操作成功");
      });

      console.log(temp);
    },
    handleClose() {
      this.$emit("handleClose");
    }
  }
};
