.mask {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, .84);
  z-index: 9;
  .mask-box {
    background: #fff;
    margin: 16% auto;
    width: 24%;
    border-radius: 8px;
    .mask-title {
      display: flex;
      padding: 14px 10px;
      font-size: 14px;
      color: #333;
      span {
        width: 50%;
        &:nth-child(2) {
          text-align: right;
          padding-right: 10px;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
    .mask-content {
      padding: 17px;
      text-align: center;
      color: #333;
      font-size: 14px;
    }
    .mask-btns {
      display: flex;
      margin-left: 50%;
      width: 50%;
      padding: 14px 0;
      font-size:12px;
      .confirm {
        border:1px solid #2589FF;
        background #2589FF;
        color: #fff;
        padding: 4px 12px;
        border-radius: 4px;
        &:hover {
          cursor: pointer;
        }
      }
      .cancel {
        border:1px solid #999999;
        background #fff;
        color: #999999;
        padding: 4px 12px;
        border-radius: 4px;
        margin-left:20px;
        &:hover {
          cursor: pointer;
        }
      }
    }
  }

}

.income-search {
  display: flex;
  box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
  padding: 8px 20px;
  margin: 10px 5px;
  border-radius: 4px;
  .search-item >>> .el-input__inner {
    height 26px;
    font-size: 12px;
    padding: 0 10px;
  }
  .search-item {
    display: flex;
    align-items center;
    margin-right: 26px;
    width: 20%;
    .label {
      font-size: 12px;
      padding-right: 4px;
      color: #333;
      font-weight: 400;
    }
    .search-btn {
      width: 30%;
      background: #009CFF;
      border: 1px solid #f5f5f5;
      text-align: center;
      border-radius: 4px;
      font-size: 12px;
      padding: 5px 1px;
      color: #fff;
      &:hover {
        cursor: pointer;
      }
    }
  }

  .search-item >>> .el-input__icon {
    line-height 26px;
  }
  .search-item >>> .el-input__prefix {
    display none;
  }

}

.dt-wrap {
  margin: 20px 5px;
  border: 1px solid #f5f5f5;
  box-shadow: 0 0 6px 0 rgba(53, 53, 53, .28);

  .dt-wrap-bar {
    padding: 12px 20px;
    font-size: 14px;
    display: flex;
    border-bottom: 1px solid #f5f5f5;

    .bar-date {
      width: 20%;
    }
    .bar-content {
      width: 60%;
    }
    .bar-action {
      width: 20%;
    }
  }
  .dt-wrap-content {
    padding: 0 20px;
    font-size: 14px;
    .content-item {
      display flex;
      align-items: center;
      padding: 14px 0;
      border-bottom: 1px solid #f5f5f5;

      .date-item {
        width: 20%;
        font-size: 12px;
      }
      .text-item {
        width: 60%;
        color: #000;
        font-size: 12px;
        font-weight: 500;
        line-height: 18px;
        .item-text {
          width: 80%;
        }
        .item-img-wrap {
          display flex;
          .img-item {
            margin-right: 10px;

            img {
              width: 50px;
              height: 50px;
              border-radius: 4px;
            }

          }
        }

      }
      .action-item {
        width: 20%;
        .action-btn {
          background: #FDF6EC;
          color: #E6A23C;
          width: 20%;
          padding: 8px 4px;
          text-align: center;
          font-size: 12px;
          border-radius: 4px;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
  }
}

.income-pagination {
  text-align center;
  margin: 20px 0;
}
