import { GetOpenidByFind } from "@/api/wechatuser";
import { PostDataByUpBind } from "@/api/shop";

import { mapGetters } from "vuex";

export default {
  name: "index",
  props: {
    postFrom: {
      type: Object
    },
    dialogVisible: {
      type: Boolean,
      default: false
    }
  },
  computed: {
    ...mapGetters(["userinfo"])
  },
  data() {
    return {
      shop: {},
      innerVisible: false,
      closeBind: true,
      againBind: true,
      openid: "",
      is_search: false,
      form: {}
    };
  },
  methods: {
    handleClose() {
      this.$emit("handleClose");
    },
    handelSave() {
      var temp = {
        user_id: this.shop.id,
        shop_id: this.postFrom.id,
        admin_id: this.userinfo.id
      };
      PostDataByUpBind(temp).then(res => {
        this.$emit("handleClose");
      });
    },
    handelconfirm() {
      if (this.is_search === false) {
        this.$message({
          message: "请先找到存在的用户",
          type: "error"
        });
        return true;
      }
      this.innerVisible = true;
    },
    handelsearch() {
      GetOpenidByFind(this.openid).then(res => {
        if (res.data === 404) {
          this.$message({
            message: "没有找到相关数据",
            type: "warning"
          });
          this.is_search = false;
          return;
        }
        this.shop = res.data;
        this.is_search = true;
        this.$message.success("操作成功");
      });
    }
  }
};
