.mask {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, .84);
  z-index: 9;
  .mask-box {
    background: #fff;
    margin: 10% auto;
    width: 40%;
    border-radius: 8px;
    .mask-title {
      display: flex;
      padding: 14px 10px;
      font-size: 14px;
      color: #333;
      span {
        width: 50%;
        &:nth-child(2) {
          text-align: right;
          padding-right: 10px;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
    .mask-content {
      padding: 17px;
      color: #333;
      font-size: 12px;
      display: flex;
      flex-wrap: wrap;
      .mask-detail {
        display: flex;
        margin: 4px 10px;
        align-items center;
        width: 44%;

        .detail-text {
          width: 30%;
          color:#333333;
        }
        .detail-value {
          width: 70%;
          background: #EEEEEE;
          padding: 5px 0 5px 10px;
        }
      }

    }
    .mask-btns {
      display: flex;
      margin-left: 80%;
      width: 26%;
      padding: 14px 0;
      font-size: 12px;
      .confirm {
        border: 1px solid #2589FF;
        background #2589FF;
        color: #fff;
        padding: 4px 12px;
        border-radius: 4px;
        &:hover {
          cursor: pointer;
        }
      }
      .cancel {
        border: 1px solid #999999;
        background #fff;
        color: #999999;
        padding: 4px 12px;
        border-radius: 4px;
        margin-left: 20px;
        &:hover {
          cursor: pointer;
        }
      }
    }
  }

}

.el-select-dropdown__item {
  color: #000;
  font-size: 12px;
}

.income-search {
  display: flex;
  box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
  padding: 8px 20px;
  margin: 10px 5px;
  border-radius: 4px;
  .search-item >>> .el-input__inner {
    height 26px;
    font-size: 12px;
    padding: 0 10px;
  }
  .search-item {
    display: flex;
    align-items center;
    margin-right: 26px;
    width: 18%;
    .label {
      font-size: 12px;
      padding-right: 4px;
      color: #333;
      font-weight: 400;
    }
    .search-btn {
      width: 30%;
      background: #009CFF;
      border: 1px solid #f5f5f5;
      text-align: center;
      border-radius: 4px;
      font-size: 12px;
      padding: 5px 1px;
      color: #fff;
      &:hover {
        cursor: pointer;
      }
    }
  }

  .search-item >>> .el-input__icon {
    line-height 26px;
  }
  .search-item >>> .el-input__prefix {
    display none;
  }

}

.income-form {
  margin: 20px 5px;
  box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
  padding: 14px 20px;
  border-radius: 4px;

  .income-table {
    margin: 0 0 20px;
  }
  .table-summary {
    display: flex;
    margin: auto;
    padding: 20px 32%;
    border-bottom: 1px solid #F5F5F5;
    .order-sum, .order-money {
      width: 50%;
      text-align center;
      span {
        &:first-child {
          font-size: 12px;
          color: #333;
        }
        &:last-child {
          font-weight: 500;
          color: #CC0000;
        }
      }
    }
  }
}

.income-pagination {
  text-align center;
  margin: 20px 0;
}
